INSERT INTO api_respuesta (id, texto, created_at, es_respuesta_correcta, activo, literal, imagen, pregunta_id) VALUES (869, 'Un electrón en un átomo se mueve en una órbita circular.Alrededor del núcleo bajo la influencia de la atracción coulómbica entre el electrón y el núcleo, obedeciendo las leyes de la mecánica clásica.', '2022-06-04 05:29:34.964927', 0, 1, 'A', '', 222);
INSERT INTO api_respuesta (id, texto, created_at, es_respuesta_correcta, activo, literal, imagen, pregunta_id) VALUES (870, ' En lugar de la infinidad de órbitas posibles en la mecánica clásica, para un electrón solo  es posible moverse en una órbita.Para la cual el momento angular L es un múltiplo entero de ħ.', '2022-06-04 05:29:34.996849', 0, 1, 'B', '', 222);
INSERT INTO api_respuesta (id, texto, created_at, es_respuesta_correcta, activo, literal, imagen, pregunta_id) VALUES (871, 'Un electrón que se mueva en una de esas órbitas permitidas no irradia energía electromagnética, aunque está siendo acelerado constantemente por las fuerzas atractivas al núcleo. Por ello, su energía total E permanece constante.', '2022-06-04 05:29:35.025527', 0, 1, 'C', '', 222);
INSERT INTO api_respuesta (id, texto, created_at, es_respuesta_correcta, activo, literal, imagen, pregunta_id) VALUES (872, 'Si un electrón que inicialmente se mueve en una órbita de energía Ei  (energía inicial), cambia discontinuamente su movimiento de forma que pasa a otra órbita de energía Ef  (energía final), se emite o absorbe energía electromagnética para compensar el cambio de la energía total. La frecuencia í de la radiación es igual a la cantidad (Ei – Ef)', '2022-06-04 05:29:35.061721', 0, 1, 'D', '', 222);
